import numpy as npimport pandas as pdimport timeimport random# [1] data preprocessing #df = pd.read_csv('./data/GSE75688_GEO_processed_Breast_Cancer_raw_TPM_matrix_gene_filtering_log.csv', index_col=0) # dimension = (genes x cells)sample_inf = pd.read_csv('./data/GSE75688_final_sample_information.csv', index_col=0) print("starting preprocessing:")n1 = sample_inf.shape[0] # row  genesn2 = sample_inf.shape[1] # col  cellscell_tumor = [] # tumor cellscell_non_tumor = [] # non tumor cellscell_bulk = [] # bulk cellscell_tumor_non_tumor = []for i in range(n1):    if sample_inf.iloc[i,0] == 'SC' and sample_inf.iloc[i,1] == 'Tumor' and sample_inf.iloc[i,2] == 'Tumor' and sample_inf.iloc[i,3] == 'Tumor':        cell_tumor.append(sample_inf.index[i])        if sample_inf.iloc[i,0] == 'SC' and sample_inf.iloc[i,1] != 'Tumor':          cell_non_tumor.append(sample_inf.index[i])             if sample_inf.iloc[i,0] == 'Bulk':        cell_bulk.append(sample_inf.index[i]) for item in cell_non_tumor:    cell_tumor_non_tumor.append(item)for item in cell_tumor:    cell_tumor_non_tumor.append(item)df1=df.loc[:,cell_tumor]df2=df.loc[:,cell_non_tumor]df3=df.loc[:,cell_tumor_non_tumor]df1.to_csv('./data/GSE75688_GEO_processed_Breast_Cancer_raw_TPM_matrix_gene_filtering_log_tumor317.csv')df2.to_csv('./data/GSE75688_GEO_processed_Breast_Cancer_raw_TPM_matrix_gene_filtering_log_non_tumor198.csv')df3.to_csv('./data/GSE75688_GEO_processed_Breast_Cancer_raw_TPM_matrix_gene_filtering_log_nontumor198tumor317.csv')