import numpy as npimport pandas as pdimport timeimport random# [1] data preprocessing #df = pd.read_csv('./data/GSE76312_Single_cell_HSCs_expression_RPKM_table_gene_filtering_log.csv', index_col=0) # dimension = (genes x cells)sample_inf = pd.read_csv('./data/GSE76312_Giustacchini-Thongjuea_et.al_Cell_Annotation.csv', index_col=0) print("starting preprocessing:")n1 = sample_inf.shape[0] # row  genesn2 = sample_inf.shape[1] # col  cellscell_normal = [] # tumor cellscell_diagnosed_tumor = [] # non tumor cellscell_normal_diagnosed_tumor = []for i in range(n1):    tmp = sample_inf.iloc[i,0]        if type(tmp) != float :        if tmp[0:6] == 'negati' or tmp[0:6] is 'positi' :            cell_diagnosed_tumor.append(sample_inf.index[i])                 if tmp[0:6] == 'normal' :            cell_normal.append(sample_inf.index[i]) for item in cell_normal:    cell_normal_diagnosed_tumor.append(item)for item in cell_diagnosed_tumor:    cell_normal_diagnosed_tumor.append(item)df1=df.loc[:,cell_diagnosed_tumor]df2=df.loc[:,cell_normal]df3=df.loc[:,cell_normal_diagnosed_tumor]df1.to_csv('./data/GSE76312_Single_cell_HSCs_expression_RPKM_table_gene_filtering_log_tumor902.csv')df2.to_csv('./data/GSE76312_Single_cell_HSCs_expression_RPKM_table_gene_filtering_log_normal232.csv')df3.to_csv('./data/GSE76312_Single_cell_HSCs_expression_RPKM_table_gene_filtering_log_normal232tumor902.csv')