import numpy as npimport pandas as pdimport timeimport random# [1] data preprocessing #df = pd.read_csv('./data/GSE72056_melanoma_single_cell_revised_v2_gene_filtering.csv', index_col=0) # dimension = (genes x cells)print("starting preprocessing:")n1 = df.shape[0] # row  genesn2 = df.shape[1] # col  cellscell_mal = [] # malignant cellscell_ben = [] # bengin cellsfor j in range(n2):   if df.iloc[1,j] == 2:       cell_mal.append(df.columns[j])    else:       cell_ben.append(df.columns[j])        df1=df.drop(columns=cell_mal)df2=df.drop(columns=cell_ben)df1.to_csv('./data/GSE72056_melanoma_single_cell_revised_v2_gene_filtering_ben3388_bkp.csv')df2.to_csv('./data/GSE72056_melanoma_single_cell_revised_v2_gene_filtering_mal1257_bkp.csv')